anger.map:
function (doc) {
   if (doc.tone) {
   		// this doc has been processed
   		var tone_id = "anger";
   		var category_id = "emotion_tone";
   		var categories = doc.tone["document_tone"]["tone_categories"];
   		for(var i=0;i<categories.length;i++){
   			var category = categories[i];
   			if( category.category_id == category_id) {
   				var tones = category.tones;
   				for(var t=0;t<tones.length;t++){
   					var tone = tones[t];
   					if( tone.tone_id == tone_id){
   						emit(tone_id, tone.score);
   					}
   				}
   			}
   		}
    }
}

